import React, { useState, useEffect } from 'react';
import './LoginPage.css'; // Importera CSS-filen

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showForgotPasswordModal, setShowForgotPasswordModal] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Lägg till logik för att hantera inloggning
    console.log('Logging in with:', email, password);
  };

  const handleForgotPassword = () => {
    setShowForgotPasswordModal(true);
  };

  const closeModal = () => {
    setShowForgotPasswordModal(false);
  };

  useEffect(() => {
    // Funktion för att visa digital klocka
    const updateClock = () => {
      const now = new Date();
      const hours = now.getHours().toString().padStart(2, '0');
      const minutes = now.getMinutes().toString().padStart(2, '0');
      const seconds = now.getSeconds().toString().padStart(2, '0');
      document.getElementById('digital-clock').textContent = `${hours}:${minutes}:${seconds}`;
    };

    setInterval(updateClock, 1000);
    updateClock(); // Kör direkt vid sidladdning

    // Variabler för analoga klockan
    const hourHand = document.querySelector('.hour-hand');
    const minuteHand = document.querySelector('.minute-hand');
    const secondHand = document.querySelector('.second-hand');

    // Funktion för att uppdatera analoga klockan
    const updateAnalogClock = () => {
      const now = new Date();
      const hours = now.getHours();
      const minutes = now.getMinutes();
      const seconds = now.getSeconds();

      const hourRotation = (hours % 12 + minutes / 60) * 30;
      const minuteRotation = (minutes + seconds / 60) * 6;
      const secondRotation = seconds * 6;

      if (hourHand) hourHand.style.transform = `rotate(${hourRotation}deg)`;
      if (minuteHand) minuteHand.style.transform = `rotate(${minuteRotation}deg)`;
      if (secondHand) secondHand.style.transform = `rotate(${secondRotation}deg)`;
    };

    // Uppdatera analoga klockan varje sekund
    setInterval(updateAnalogClock, 1000);
    updateAnalogClock(); // Kör direkt vid sidladdning
  }, []);

  return (
    <div className="login-container">
      <div className="clock-container">
        <div className="analog-clock">
          <div className="hour-hand"></div>
          <div className="minute-hand"></div>
          <div className="second-hand"></div>
        </div>
        <div className="digital-clock" id="digital-clock"></div>
      </div>
      <div className="login-box">
        <img src="logo.png" alt="Företagslogga" className="logo" id="login-logo" />
        <h1 id="welcome-text">Välkommen!</h1>
        <form id="login-form" onSubmit={handleSubmit}>
          <div className="input-group">
            <i className="fas fa-user"></i>
            <input
              type="text"
              id="email"
              name="email"
              placeholder="Användarnamn"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <i className="fas fa-lock"></i>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Lösenord"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button id="login-button" type="submit">Logga in</button>
        </form>
        <a href="#" id="forgot-password" onClick={handleForgotPassword}>Glömt lösenord?</a>
      </div>
      {showForgotPasswordModal && (
        <div id="forgot-password-modal" className="modal">
          <div className="modal-content">
            <span className="close-button" onClick={closeModal}>&times;</span>
            <h2>Återställ lösenord</h2>
            <input type="email" id="reset-email" placeholder="Jobbmejl" required />
            <button id="send-reset-link">Skicka återställningslänk</button>
            <div id="reset-message"></div>
            <div id="new-password-section" style={{ display: 'none' }}>
              <input type="password" id="new-password" placeholder="Nytt lösenord" required />
              <button id="confirm-new-password">Bekräfta nytt lösenord</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LoginPage;
